        return new Source("{% extends 'Front/navigation/index.html.twig' %}

{% block title %}Activite index{% endblock %}

{% block body %}
    <h1>Activite index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Opérations</th>
            </tr>
        </thead>
        <tbody>
        {% for activite in activites %}
            <tr>
                <td>{{ activite.id }}</td>
                <td>{{ activite.name }}</td>
                <td>
                    <a href=\"{{ path('activite_show', {'id': activite.id}) }}\">Détails</a>
                   {% if is_granted(\"ROLE_AGENCE\") %}
                        <a href=\"{{ path('activite_edit', {'id': activite.id}) }}\">Modifier</a>
                    {% endif %}
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"3\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
    {% if is_granted(\"ROLE_AGENCE\") %}
        <a href=\"{{ path('activite_new') }}\">Créer une nouvelle activité</a>
    {% endif %}
{% endblock %}
